// Parser do output do Agente Analisador
const output = $input.first().json.output || '';
const originalBody = $('Webhook').first().json.body || {};

try {
    let cleanOutput = output
        .replace(/^```json\s*/i, '')
        .replace(/^```\s*/i, '')
        .replace(/```\s*$/i, '')
        .trim();

    const parsed = JSON.parse(cleanOutput);

    // Determina se precisa buscar dados
    const requiresData = parsed.requires_data === true || 
                         (parsed.data_needs && parsed.data_needs.length > 0);

    return {
        json: {
            success: true,
            intent: parsed.intent || 'busca_generica',
            requires_data: requiresData,
            confidence: parsed.confidence || 0.5,
            entities: parsed.entities || {},
            data_needs: parsed.data_needs || [],
            filters: parsed.filters || { limit: 10 },
            requires_aggregation: parsed.requires_aggregation || false,
            original_question: parsed.original_question || originalBody.message || '',
            original_body: originalBody,
            sessionId: originalBody.user_id || null
        }
    };
} catch (error) {
    // Em caso de erro de parse, assume que N√ÉO precisa de dados
    return {
        json: {
            success: false,
            intent: 'conversa',
            requires_data: false,
            confidence: 0.3,
            entities: {},
            data_needs: [],
            filters: {},
            requires_aggregation: false,
            original_question: originalBody.message || '',
            original_body: originalBody,
            sessionId: originalBody.user_id || null,
            parse_error: error.message
        }
    };
}

// Processa dados do Supabase (tabela dados_estoque) e monta contexto
const allItems = $input.all();
const parserData = $('Parser Analisador').first().json;

// Filtra apenas registros DETALHE (ignora SUMARIO)
let produtos = allItems.map(item => item.json).filter(p => p.tipo_registro === 'DETALHE');

// Se tem filtro de busca, filtra localmente
const searchTerm = parserData.filters?.search_term;
if (searchTerm && produtos.length > 0) {
    const searchPattern = searchTerm.replace(/%/g, '').toLowerCase();
    const terms = searchPattern.split(/\s+/).filter(t => t.length > 0);

    produtos = produtos.filter(p => {
        const nome = (p.produto_descricao || '').toLowerCase();
        return terms.every(term => nome.includes(term));
    });
}

// Se tem filtro de status, filtra
const statusFilter = parserData.filters?.status;
if (statusFilter && produtos.length > 0) {
    produtos = produtos.filter(p => {
        const status = (p.status_ruptura || '').toUpperCase();
        return status.includes(statusFilter.toUpperCase());
    });
}

// Limita resultados
const limit = parserData.filters?.limit || 10;
produtos = produtos.slice(0, limit);

// Calcula agrega√ß√µes se necess√°rio
let financeiro = null;
if (parserData.requires_aggregation) {
    const parseNum = (val) => {
        if (!val) return 0;
        if (typeof val === 'number') return val;
        return parseFloat(val.toString().replace(/\./g, '').replace(',', '.')) || 0;
    };

    const allProducts = allItems.map(i => i.json).filter(p => p.tipo_registro === 'DETALHE');
    let totalEstoque = 0, totalReceita = 0, rupturas = 0, excessos = 0, criticos = 0, atencao = 0, saudaveis = 0;

    allProducts.forEach(item => {
        const qty = parseNum(item.estoque_atual);
        const custo = parseNum(item.custo);
        const preco = parseNum(item.preco);
        const status = (item.status_ruptura || '').toLowerCase();

        totalEstoque += qty * custo;
        totalReceita += qty * preco;
        if (status.includes('excesso')) excessos++;
        if (status.includes('cr√≠tico') || status.includes('critico')) criticos++;
        if (status.includes('aten√ß√£o') || status.includes('atencao')) atencao++;
        if (status.includes('saud√°vel') || status.includes('saudavel')) saudaveis++;
    });

    financeiro = {
        total_skus: allProducts.length,
        valor_estoque: totalEstoque.toFixed(2),
        receita_potencial: totalReceita.toFixed(2),
        lucro_projetado: (totalReceita - totalEstoque).toFixed(2),
        margem_media: totalReceita > 0 ? ((totalReceita - totalEstoque) / totalReceita * 100).toFixed(1) : 0,
        distribuicao: { excessos, criticos, atencao, saudaveis }
    };
}

// Monta payload enriquecido com campos corretos da tabela dados_estoque
const produtosFormatados = produtos.map(p => ({
    nome: p.produto_descricao,
    id: p.id_produto,
    estoque: p.estoque_atual,
    demanda_dia: p.media_diaria_venda,
    cobertura_dias: p.dias_de_cobertura,
    status: p.status_ruptura,
    preco: p.preco,
    custo: p.custo
}));

const contextoDados = financeiro 
    ? `\n## Dados Financeiros\n${JSON.stringify(financeiro, null, 2)}\n\n## Produtos (${produtosFormatados.length} encontrados)\n${JSON.stringify(produtosFormatados.slice(0, 5), null, 2)}`
    : `\n## Produtos Encontrados (${produtosFormatados.length})\n${JSON.stringify(produtosFormatados.slice(0, 10), null, 2)}`;

return {
    json: {
        route: mapIntentToRoute(parserData.intent),
        original_body: parserData.original_body,
        payload_string: `# PERGUNTA DO USU√ÅRIO\n${parserData.original_question}\n\n# DADOS REAIS DO SISTEMA SmartOrders${contextoDados}\n\n# INSTRU√á√ïES\nResponda usando APENAS os dados acima. Seja preciso e cite n√∫meros.`,
        sessionId: parserData.sessionId,
        intent: parserData.intent,
        has_data: true,
        timestamp: new Date().toISOString()
    }
};

function mapIntentToRoute(intent) {
    const map = {
        'campanha_marketing': 'MARKETING',
        'sugestao_compra': 'COMPRA',
        'negociacao': 'NEGOCIACAO',
        'relatorio_geral': 'RELATORIO',
        'consulta_ruptura': 'ESTOQUE',
        'consulta_excesso': 'ESTOQUE',
        'consulta_produto': 'ESTOQUE',
        'consulta_estoque': 'ESTOQUE'
    };
    return map[intent] || 'ESTOQUE';
}

// Monta contexto para conversa SEM busca de dados
const parserData = $('Parser Analisador').first().json;

const intentMessages = {
    'agradecimento': 'O usu√°rio est√° agradecendo. Responda de forma cordial e pergunte se pode ajudar com mais alguma coisa.',
    'esclarecimento': 'O usu√°rio quer um esclarecimento sobre algo que foi dito anteriormente. Se precisar de dados espec√≠ficos, pe√ßa que especifique qual produto ou informa√ß√£o deseja consultar.',
    'conversa': 'Continue a conversa de forma natural. Se o usu√°rio precisar de dados de produtos, pe√ßa que especifique qual produto ou informa√ß√£o deseja.'
};

const instrucao = intentMessages[parserData.intent] || 
    'Responda de forma natural e conversacional. Se o usu√°rio precisar de dados de produtos, pe√ßa que especifique qual produto ou informa√ß√£o deseja consultar.';

return {
    json: {
        route: 'ESTOQUE', // Agente padr√£o para conversas
        original_body: parserData.original_body,
        payload_string: `# MENSAGEM DO USU√ÅRIO\n${parserData.original_question}\n\n# CONTEXTO\nEsta √© uma mensagem conversacional. N√ÉO h√° dados novos do banco de dados nesta resposta.\n\n# INSTRU√á√ïES\n${instrucao}\n\nSeja natural, amig√°vel e prestativo. Se n√£o tiver informa√ß√µes suficientes para responder uma pergunta espec√≠fica, pe√ßa que o usu√°rio especifique qual produto ou dado deseja consultar.`,
        sessionId: parserData.sessionId,
        intent: parserData.intent,
        has_data: false,
        timestamp: new Date().toISOString()
    }
};

